mapper.select:
select s.*, d.department_name from staff s, department d
where s.department_id = d.department_id  
and staff_id = #{id} and pw = #{pw}

select mr.medical_record_id, p.name as patient_name, s.name as staff_name, mr.memo, to_char(mr.treatment_date, 'yyyy-mm-dd hh24:mi') as treatment_date, mr.admission, mr.treatment_name, pr.prescription_record_id from patient p, staff s, medical_record mr, prescription_record pr where p.patient_id = mr.patient_id and s.staff_id = mr.staff_id and pr.medical_record_id(+) = mr.medical_record_id and mr.treatment_date between #{first_date} and #{second_date}
and s.staff_id in ( select staff_id from staff where department_id = (select department_id from staff where staff_id = #{id}) and staff_level = 1 )
and s.staff_id = #{id}
and p.name like '%' || #{patient_name} || '%'
order by mr.treatment_date

choose.when:
	select schedule_id, staff_id, content, to_char(time, 'hh24:mi') as time 
    from doctor_schedule where staff_id = #{id} and to_char(time, 'yyyy-mm-dd') = #{date} 
    order by time

select schedule_id, staff_id, content, complete, to_char(time, 'hh24:mi') as time 
from nurse_schedule where staff_id = #{id} and to_char(time, 'yyyy-mm-dd') = #{date}
order by time
