mapper.select:
SELECT p.*, a.*
FROM patient p INNER JOIN patient_account a ON p.patient_id = a.patient_id  
WHERE a.email = #{email} AND a.pw = #{pw}

SELECT * 
FROM patient 
WHERE name = #{name} AND social_id = #{social_id}

SELECT email
FROM patient_account
WHERE email = #{email}

SELECT p.*, a.*
FROM patient p INNER JOIN patient_account a ON p.patient_id = a.patient_id  
WHERE p.patient_id = #{patient_id}
